package com.test_array_06;

/*
 * 使用数组，在数组元素中保存1到10，然后将所有数组元素相乘并输出结果。
 */
public class test1 {
	public static void main(String[] args) {
		int[] arr = new int[10];
		for (int i = 0; i < arr.length; i++) {
			arr[i] = i + 1;
		}

		int result = Sum(arr);
		System.out.println("所有数组元素相乘结果是：" + result);

	}

	private static int Sum(int[] arr) {
		// TODO Auto-generated method stub
		int j = 1;

		for (int i = 0; i < arr.length; i++) {
			j *= arr[i];
		}
		return j;
	}
}
